mapper.select:
select *
  from member
 where email=#{email}
   and pwd=#{pwd}

mapper.update:
update member
   set pwd=#{pwd}, tel=#{tel},
   		post1=#{post1}, post2=#{post2}, addr1=#{addr1}, addr2=#{addr2}
 where email=#{email}

mapper.select:
select *
  from member
where name=#{name}
and tel =#{tel}
